<template>
  <div>
  <nav class="bar bar-nav">
    <h1 class="title">编辑自选</h1>
    <span class="button-nav pull-right pull-del" @click="del">
      <span class="icon"></span>
    </span>
    <span class="button-nav pull-right pull-add" v-link="{path:'/hq/search'}">
      <span class=" icon  icon-white"></span>
    </span>

    <span class="button-nav pull-left" @click="goback()"><span class="icon text">完成</span></span>
  </nav>
  <div class="content">
    <div class="fund_table edit">
      <dl class="title">
        <dd></dd>
        <dt>证券名称</dt>
        <dd>置顶</dd>
        <!--<dd>拖动</dd>-->
      </dl>
    </div>
    <div class="fund_table tableList edit">
      <dl class="part" v-for="items in grid">
        <dd class="ddCheck" @click="check($index)">
          <span class="check circle {{items.actived == true?'checked':''}}"></span>
        </dd>
        <dt>
          <strong>{{items.stockname}}</strong>
          <small>{{items.stockcode}}</small>
        </dt>
        <dd>
          <span class="icon_top" @click="top($index)"></span>
        </dd>
        <!--<dd><span class="icon_move"></span></dd>-->
      </dl>
    </div>
  </div>
  </div>
  <div class="footer"><span @click.stop="upZXG(0)">上传</span><span @click.stop="loading">下载</span><span @click.stop="upZXG(2)">覆盖</span></div>
</template>
<script>
  import tztZXstock from '../scripts/tztZXstock'
  import service_hq_often from '../service/common/service_hq_often';
  import localService from '../../../services/localService'
	export default {
		data() {
		  return {
		    grid:[],
            account:'',
            market:''
			}
		},
    attached(){
		  this.inits();
    },
    methods:{
      inits(){
        var that = this;
        tztZXstock.getZXList(function ( grid) {
          var zxlist =decodeURIComponent(encodeURIComponent(grid).replace(/%00/g,'')).split(',');
          var ln = zxlist.length;
          var zxArray = [];
          for(var i=0;i<ln;i++){
            var list = zxlist[i].split('|');
            if(list[0] == '' && list[2] == '' && list[1] == ''){
              continue;
            }
            var obj = {
              stockcode:list[0],
              stockname:list[2].replace(/(\S+\S)(.+)(\S+\S)(.+)/g,'$1$3'),
              markettype:list[1],
              actived:false
            };
            zxArray.push(obj);
          }
          that.grid = zxArray;
        })
      },
      top(index){
        var xArray = this.grid;
        var list = xArray[index];
        var nArray1 = xArray.slice(0,index);
        var nArray2 = xArray.slice(index+1);
        var stockArray = nArray1.concat(nArray2);
        stockArray.unshift(list);
        this.$set('grid',stockArray);
        tztZXstock.setAllZXList(stockArray);
      },
      //下载自选股
      loading(){
        var thats = this;
        localService.readMapMesg(['jyloginflag','account','acitve_phone'])
                .then((local)=>{
          console.log(local );
            if(local.jyloginflag && local.jyloginflag >= 1){
              $.confirm("确定从服务器端下载个人自选股", function () {
                var obj = {
                  MaxCount:200,
                  MobileCode:local.acitve_phone,
                  Account:local.account
                };
                service_hq_often.require56(obj)
                        .then((data)=> {
                  if(data.ERRORNO>0){
                  $.alert('下载完成！');
                }
                  if(!data.GRID0){
                  return;
                }
                console.log(data.GRID0)

                var osend = {
                  Grid:data.GRID0,
                  StockIndex:1,
                  StartPos:0,
                  NewMarketNo:0,
                  MaxCount:200
                };
                var that = this;
                service_hq_often.require60(osend)
                        .then((oData)=> {
                  console.log(oData)
                oData.GRID0.shift();
                var newData = oData.GRID0;
                var NewMarketNo = oData.NEWMARKETNO.split('|');
                for(var i=0;i<newData.length;i++){
                  var listData = newData[i].split('|');

                  tztZXstock.setZXList(listData[oData.STOCKCODEINDEX],NewMarketNo[i],listData[oData.STOCKNAMEINDEX].replace(/^\d+./g,'')); //设置自选股
                }
                thats.inits();
              })
              });
              })
            }else{
              this.$TZT.action10090({url:"/hq/editzxg"});
            }

          });


      },
      //上传 合并 自选股
      upZXG(index){
        var that = this;
        var that = this;
        localService.readMapMesg(['jyloginflag','account','acitve_phone'])
                .then((local)=> {
          console.log(local);
        if (local.jyloginflag && local.jyloginflag >= 1) {
          var xArray = that.grid;
          var ln = xArray.length;
          var upArray = [];
          for(var i=0;i<ln;i++){
            var list = xArray[i];
            upArray.push(list.stockcode+'|'+list.markettype);
          }
          var cont = '';
          if(upArray.length>0){
            if(index=='0'){
              cont = '确定上传当前页面自选股至服务器端?';
            }else {
              cont = '确定上传当前页面自选股覆盖服务器自选股?';
            }
            $.confirm(cont, function () {
              var obj = {
                Account:local.account,
                MobileCode:local.acitve_phone,
                Grid:upArray,
                Direction:index
              };
              service_hq_often.require55(obj)
                      .then((data)=> {
                console.log(data);
              if(data.ERRORNO>0){
                $.alert('操作成功！');
              }
            });
              that.inits();
            });
          }
        }else{
          this.$TZT.action10090({url:"/hq/editzxg"});
        }
      })

      },
      del(){
        var that = this;
        var xArray = that.grid;
        var ln = xArray.length;
        var delArray = [];
        for(var i=0;i<ln;i++){
          var list = xArray[i];
          if(list.actived){
            delArray.push(list.stockcode);
          }
        }
        if(delArray.length>0){
          $.confirm('确定删除所选自选股?', function () {
            tztZXstock.delZXList(delArray);
            that.inits();
          });
        }else {
          $.alert('请选择要删除的自选股!');
        }
      },
      check(index){
        if(this.grid[index].actived){
          this.grid[index].actived = false;
        }else{
          this.grid[index].actived = true;
        }
      },
      goback(){
        window.history.back();
      },
      clickFunc(index){
        this.$TZT.action10061('/hq/list?market='+this.grid[index]['market']);
      }
    },
    events:{

    }
	}
</script>
